mapper.select:
select
main_order_id, shop_id, user_id, send_address, mobile, remarks, status, pay_type, pay_way, wx_pay_order,total_price, evaluate, ctime,order_code,receive
from wx_shop.main_order


        and main_order_id = #{mainOrderId}


        and shop_id = #{shopId}


        and user_id = #{userId}


        and send_address = #{sendAddress}


        and mobile = #{mobile}


        and remarks = #{remarks}


        and status = #{status}


        and pay_type = #{payType}


        and pay_way = #{payWay}


        and wx_pay_order = #{wxPayOrder}


        and order_code = #{orderCode}


        and evaluate = #{evaluate}


        and ctime = #{ctime}


order by main_order_id desc
limit #{offset}, #{limit}

select
  main_order_id, shop_id, user_id, send_address, mobile, remarks, status, pay_type, pay_way, wx_pay_order,total_price, evaluate, ctime,order_code,receive
from wx_shop.main_order

    and status!=404

        and main_order_id = #{mainOrderId}


        and shop_id = #{shopId}


        and user_id = #{userId}


        and send_address = #{sendAddress}


        and mobile = #{mobile}


        and remarks = #{remarks}


        and status = #{status}


        and pay_type = #{payType}


        and pay_way = #{payWay}


        and wx_pay_order = #{wxPayOrder}


        and order_code = #{orderCode}


        and evaluate = #{evaluate}


        and ctime = #{ctime}


order by main_order_id desc

select
count(*)
from wx_shop.main_order


        and main_order_id = #{mainOrderId}


        and shop_id = #{shopId}


