mapper.select:
select
  id, appid, openid, meat_day, meat_time, feedback_type, problem_description, content, image, phone, ctime, status
from wx_order.wx_feedback


        and id = #{id}


        and appid = #{appid}


        and openid = #{openid}


        and meat_day = #{meatDay}


        and meat_time = #{meatTime}


        and feedback_type = #{feedbackType}


        and problem_description = #{problemDescription}


        and content = #{content}


        and image = #{image}


        and phone = #{phone}


        and ctime = #{ctime}


        and status = #{status}

