mapper.select:
select
  user_id, introducer_id, shop_id, nick_name, avatar_url, city, province, country, phone, openid, gender, ctime
from wx_order.wx_user


        and user_id = #{userId}


        and introducer_id = #{introducerId}


        and shop_id = #{shopId}


        and nick_name = #{nickName}


        and avatar_url = #{avatarUrl}


        and city = #{city}


        and province = #{province}


        and country = #{country}


        and phone = #{phone}


        and openid = #{openid}


        and gender = #{gender}


        and ctime = #{ctime}


order by ctime desc

